    /**
     * Get the sprite array for the specified dice value (1-6)
     */
    public static int[] getDiceSprite(int value) {
        switch (value) {
            case 1: return ONE;
            case 2: return TWO;
            case 3: return THREE;
            case 4: return FOUR;
            case 5: return FIVE;
            case 6: return SIX;
            default: return ONE; // fallback
        }
    }
